(function($) {

  /**
   * Copyright 2012, Digital Fusion
   * Licensed under the MIT license.
   * http://teamdf.com/jquery-plugins/license/
   *
   * @author Sam Sehnert
   * @desc A small plugin that checks whether elements are within
   *     the user visible viewport of a web browser.
   *     only accounts for vertical position, not horizontal.
   */

  $.fn.visible = function(partial) {

      var $t            = $(this),
          $w            = $(window),
          viewTop       = $w.scrollTop(),
          viewBottom    = viewTop + $w.height(),
          _top          = $t.offset().top,
          _bottom       = _top + $t.height(),
          compareTop    = partial === true ? _bottom : _top,
          compareBottom = partial === true ? _top : _bottom;

    return ((compareBottom <= viewBottom) && (compareTop >= viewTop));

  };

})(jQuery);

$(window).scroll(function(event) {

  $(".module").each(function(i, el) {
    var el = $(el);
    if (el.visible(true) && !(el.hasClass("swipe"))) {
      el.addClass("come-in");
    }
  });

});

var win = $(window);
var allMods = $(".module");

// Already visible modules
allMods.each(function(i, el) {
  var el = $(el);
  if (el.visible(true)) {
    el.addClass("already-visible");
  }
});

win.scroll(function(event) {

  allMods.each(function(i, el) {
    var el = $(el);
    if (el.visible(true)) {
      if(el.hasClass("swipe")) {
        if(el.hasClass("even-item")) {
          el.addClass("come-in-swipe-even");
        } else {
            el.addClass("come-in-swipe");
        }

      } else {
        el.addClass("come-in");
        el.removeClass("fading-div");
      }
    }
  });

});


$(function() {

    var $meters = $(".meter > span");
    var $section = $('.skill-bars');
    var $queue = $({});

    function loadDaBars() {
				$(".meter > span").each(function() {
          bar_width = $(this).attr('aria-valuenow') + '%';
          console.log(bar_width);
					$(this)
						.data("origWidth", $(this).width())
						.width(0)
						.animate({
							width: bar_width
						}, 1200);
				});
    }

    $(document).bind('scroll', function(ev) {
        var scrollOffset = $(document).scrollTop();
        var containerOffset = $section.offset().top - window.innerHeight;
        if (scrollOffset > containerOffset) {
            loadDaBars();
            // unbind event not to load scrolsl again
            $(document).unbind('scroll');
        }
    });

});
